import React from 'react';
import { COLORS } from '../../constants';
import { ChevronLeft } from 'lucide-react';

const Header = ({ title, actions, onBack, style, className }) => {
  return (
    <div 
      className={`view-header ${className || ''}`}
      style={{ 
        display: 'flex', 
        flexDirection: 'column', 
        width: '100%', 
        boxSizing: 'border-box',
        ...style 
      }}
    >
      <div style={{ 
        display: 'flex', 
        alignItems: 'center', // Vertically center everything
        paddingBottom: '10px',
        minHeight: '40px',
        position: 'relative'
      }}>

        {/* LEFT SLOT: Reserved space (40px) for Back Icon */}
        {/* This ensures the Title always starts at the same X-coordinate */}
        <div style={{ 
            width: '40px', 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'flex-start',
            flexShrink: 0 
        }}>
            {onBack && (
                <button 
                    onClick={onBack}
                    style={{ 
                        background: 'none', 
                        border: 'none', 
                        color: COLORS.primary, 
                        cursor: 'pointer', 
                        padding: 0,
                        display: 'flex', 
                        alignItems: 'center'
                    }}
                >
                    <ChevronLeft size={32} />
                </button>
            )}
        </div>

        {/* CENTER SLOT: Title */}
        <div style={{ flex: 1, textAlign: 'left' }}>
            <h2 style={{ margin: 0, color: 'white', fontSize: '24px' }}>{title}</h2>
        </div>

        {/* RIGHT SLOT: Actions */}
        {actions && (
          <div style={{ display: 'flex', gap: '10px', alignItems: 'center', marginLeft: 'auto' }}>
            {actions}
          </div>
        )}
      </div>

      <div style={{ 
        height: '1px', 
        width: '100%', 
        backgroundColor: COLORS.border 
      }} />
    </div>
  );
};

export default Header;
