/* --- React and Firebase Imports --- */
// No changes here
import React, { useState, useEffect } from 'react';
import './App.css';
import {
  createUserWithEmailAndPassword,
  signOut,
  signInWithEmailAndPassword,
  onAuthStateChanged,
  updateEmail,
  EmailAuthProvider,
  reauthenticateWithCredential
} from "firebase/auth";
import { doc, setDoc, getDoc, updateDoc } from "firebase/firestore";
import { auth, db } from "./firebase";

/* --- The Main App Component --- */
function App() {

  /* --- State Variables --- */
  // (All your state variables are unchanged)
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [activeView, setActiveView] = useState('profile');
  const [authView, setAuthView] = useState('signUp');
  const [isLoading, setIsLoading] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [playerName, setPlayerName] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [notificationPreference, setNotificationPreference] = useState("Email");
  const [comments, setComments] = useState("");
  const [isEditingProfile, setIsEditingProfile] = useState(false);
  const [profileFormData, setProfileFormData] = useState({});
  const [needsReauth, setNeedsReauth] = useState(false);
  const [reauthPassword, setReauthPassword] = useState("");
  const [soccerDetails, setSoccerDetails] = useState(null);
  const [soccerFormData, setSoccerFormData] = useState({
    favoredPosition: "",
    jerseySize: "Large",
    playerNumber: 0,
    comments: "",
    currentRosters: "",
    rosterJerseysOwned: ""
  });
  const [isEditingSoccer, setIsEditingSoccer] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  /* --- useEffect Hooks --- */
  // (All your useEffect hooks are unchanged)
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        const userDocRef = doc(db, "users", user.uid);
        const userDoc = await getDoc(userDocRef);
        const soccerDocRef = doc(db, "users", user.uid, "sportsDetails", "soccer");
        const soccerDoc = await getDoc(soccerDocRef);

        if (userDoc.exists()) {
          setLoggedInUser(userDoc.data());
          setActiveView('profile');
        } else {
          console.error("No user profile found in Firestore!");
          setLoggedInUser(null);
        }

        if (soccerDoc.exists()) {
          setSoccerDetails(soccerDoc.data());
        } else {
          setSoccerDetails(null);
        }
      } else {
        setLoggedInUser(null);
        setSoccerDetails(null);
      }
      setIsLoading(false);
    });
    return () => unsubscribe();
  }, []);

  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth < 768;
      setIsMobile(mobile);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  /* --- ALL OTHER FUNCTIONS (handleSignUp, handleSignIn, etc.) --- */
  // (All your handler functions are unchanged)

  const handleSignUp = async (e) => {
    e.preventDefault();
    if (password.length < 6) {
      alert("Password must be at least 6 characters long.");
      return;
    }
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      const userProfileData = {
        uid: user.uid,
        playerName: playerName,
        email: email,
        phone: phone,
        address: address,
        notificationPreference: notificationPreference,
        comments: comments,
        isAdmin: false
      };
      await setDoc(doc(db, "users", user.uid), userProfileData);
      setLoggedInUser(userProfileData);
      setActiveView('profile');
      setEmail("");
      setPassword("");
      setPlayerName("");
      setPhone("");
      setAddress("");
      setNotificationPreference("Email");
      setComments("");
    } catch (error) {
      console.error("Error signing up:", error);
      alert("Error: " + error.message);
    }
  };

  const handleSignIn = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      const userDocRef = doc(db, "users", user.uid);
      const userDoc = await getDoc(userDocRef);
      const soccerDocRef = doc(db, "users", user.uid, "sportsDetails", "soccer");
      const soccerDoc = await getDoc(soccerDocRef);
      if (soccerDoc.exists()) {
        setSoccerDetails(soccerDoc.data());
      } else {
        setSoccerDetails(null);
      }
      if (userDoc.exists()) {
        setLoggedInUser(userDoc.data());
        setActiveView('profile');
      } else {
        alert("Authentication successful, but no profile was found.");
        setLoggedInUser(null);
      }
      setEmail("");
      setPassword("");
    } catch (error) {
      console.error("Error signing in:", error);
      alert("Error: " + error.message);
    }
    setIsLoading(false);
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
      setLoggedInUser(null);
      setSoccerDetails(null);
      setActiveView('profile');
      setAuthView('signIn');
    } catch (error) {
      console.error("Error signing out:", error);
      alert("Error: " + error.message);
    }
  };

  const handleProfileFormChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    if (!loggedInUser) return;
    if (profileFormData.email !== loggedInUser.email) {
      try {
        await updateEmail(auth.currentUser, profileFormData.email);
        console.log("Email updated in Firebase Auth!");
      } catch (error) {
        console.error("Error updating email:", error);
        if (error.code === 'auth/requires-recent-login') {
          alert("This is a sensitive operation. Please re-enter your password to confirm the change.");
          setNeedsReauth(true);
        } else {
          alert("Error: " + error.message);
        }
        return;
      }
    }
    try {
      const dataToUpdate = {
        playerName: profileFormData.playerName,
        phone: profileFormData.phone,
        address: profileFormData.address,
        notificationPreference: profileFormData.notificationPreference,
        comments: profileFormData.comments,
        email: profileFormData.email
      };
      const userDocRef = doc(db, "users", loggedInUser.uid);
      await updateDoc(userDocRef, dataToUpdate);
      setLoggedInUser(prevUser => ({
        ...prevUser,
        ...dataToUpdate
      }));
      setIsEditingProfile(false);
      alert("Profile successfully updated!");
    } catch (error) {
      console.error("Error updating profile in Firestore:", error);
      alert("Error saving profile: " + error.message);
    }
  };

  const handleReauthSubmit = async (e) => {
    e.preventDefault();
    if (!auth.currentUser || !reauthPassword) return;
    try {
      const credential = EmailAuthProvider.credential(
        auth.currentUser.email,
        reauthPassword
      );
      await reauthenticateWithCredential(auth.currentUser, credential);
      console.log("User re-authenticated!");
      await updateEmail(auth.currentUser, profileFormData.email);
      console.log("Email update successful after re-auth!");
      const userDocRef = doc(db, "users", loggedInUser.uid);
      await updateDoc(userDocRef, {
        email: profileFormData.email
      });
      setLoggedInUser(prevUser => ({
        ...prevUser,
        email: profileFormData.email
      }));
      alert("Email successfully updated!");
      setNeedsReauth(false);
      setReauthPassword("");
      setIsEditingProfile(false);
    } catch (error) {
      console.error("Error during re-authentication:", error);
      alert("Error: Incorrect password or another error occurred. " + error.message);
    }
  };

  const handleSoccerSubmit = async (e) => {
    e.preventDefault();
    if (!loggedInUser) {
      alert("You must be logged in to save data.");
      return;
    }
    try {
      const soccerDataToSave = {
        ...soccerFormData,
        currentRosters: soccerFormData.currentRosters.split(',').map(item => item.trim()),
        rosterJerseysOwned: soccerFormData.rosterJerseysOwned.split(',').map(item => item.trim()),
        playerNumber: Number(soccerFormData.playerNumber) || 0,
      };
      const soccerDocRef = doc(db, "users", loggedInUser.uid, "sportsDetails", "soccer");
      await setDoc(soccerDocRef, soccerDataToSave);
      setSoccerDetails(soccerDataToSave);
      setIsEditingSoccer(false);
      alert("Soccer info saved!");
    } catch (error) {
      console.error("Error saving soccer details:", error);
      alert("Error: " + error.message);
    }
  };

  const handleSoccerFormChange = (e) => {
    const { name, value } = e.target;
    setSoccerFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  /* --- Helper Function to Render Main Content --- */
  const renderActiveView = () => {
    switch (activeView) {
      case 'profile':
        if (isEditingProfile) {
          // The "Edit Profile" FORM is unchanged
          return (
            <form onSubmit={handleProfileSubmit} style={{
              display: 'flex', flexDirection: 'column', gap: '15px',
              maxWidth: '400px', textAlign: 'left'
            }}>
              <h2>Edit My Profile</h2>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Player Name:
                <input
                  type="text"
                  name="playerName"
                  value={profileFormData.playerName}
                  onChange={handleProfileFormChange}
                  required
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Email:
                <input
                  type="email"
                  name="email"
                  value={profileFormData.email}
                  onChange={handleProfileFormChange}
                  required
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Phone:
                <input
                  type="tel"
                  name="phone"
                  value={profileFormData.phone}
                  onChange={handleProfileFormChange}
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Address:
                <input
                  type="text"
                  name="address"
                  value={profileFormData.address}
                  onChange={handleProfileFormChange}
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Notification Preference:
                <select
                  name="notificationPreference"
                  value={profileFormData.notificationPreference}
                  onChange={handleProfileFormChange}
                  style={{ padding: '8px' }}
                >
                  <option value="Email">Email</option>
                  <option value="Text Message">Text Message</option>
                </select>
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Comments:
                <textarea
                  name="comments"
                  value={profileFormData.comments}
                  onChange={handleProfileFormChange}
                  placeholder="Feel free to contact me anytime."
                  style={{ padding: '8px', minHeight: '60px' }}
                />
              </label>
              <div style={{ display: 'flex', gap: '10px' }}>
                <button type="submit" style={{
                  padding: '10px 20px', backgroundColor: '#61dafb', border: 'none',
                  cursor: 'pointer', fontSize: '16px'
                }}>
                  Save Profile
                </button>
                <button
                  type="button"
                  onClick={() => setIsEditingProfile(false)}
                  style={{
                    padding: '10px 20px', backgroundColor: '#555', border: 'none',
                    color: 'white', cursor: 'pointer', fontSize: '16px'
                  }}>
                  Cancel
                </button>
              </div>
            </form>
          );
        }
        return (
          // --- NEW: This is the updated "My Profile" view ---
          <div style={{ textAlign: 'left', maxWidth: '500px' }}>
            <h2>My Profile</h2>

            {/* NEW: Replaced <p> tags with our new table layout */}
            <div className="info-table">
              <div className="info-label">Player Name:</div>
              <div className="info-value">{loggedInUser.playerName}</div>

              <div className="info-label">Email:</div>
              <div className="info-value">{loggedInUser.email}</div>

              <div className="info-label">Phone:</div>
              <div className="info-value">{loggedInUser.phone}</div>

              <div className="info-label">Address:</div>
              <div className="info-value">{loggedInUser.address}</div>

              <div className="info-label">Preference:</div>
              <div className="info-value">{loggedInUser.notificationPreference}</div>

              <div className="info-label">Comments:</div>
              <div className="info-value">{loggedInUser.comments}</div>

              <div className="info-label">Is Admin:</div>
              <div className="info-value">{loggedInUser.isAdmin ? 'Yes' : 'No'}</div>
            </div>

            <button
              onClick={() => {
                setProfileFormData(loggedInUser);
                setIsEditingProfile(true);
              }}
              style={{
                padding: '10px 20px', backgroundColor: '#61dafb', border: 'none',
                cursor: 'pointer', fontSize: '16px', marginTop: '30px' // Added more top margin
              }}>
              Edit Profile
            </button>
          </div>
        );

      case 'sports':
        if (isEditingSoccer) {
          // The "Edit Sports" FORM is unchanged
          return (
            <form onSubmit={handleSoccerSubmit} style={{
              display: 'flex', flexDirection: 'column', gap: '15px',
              maxWidth: '400px', textAlign: 'left'
            }}>
              <h2>Enter Soccer Details</h2>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Favored Position:
                <input
                  type="text"
                  name="favoredPosition"
                  value={soccerFormData.favoredPosition}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Jersey Size:
                <select
                  name="jerseySize"
                  value={soccerFormData.jerseySize}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px' }}
                >
                  <option value="Small">Small</option>
                  <option value="Medium">Medium</option>
                  <option value="Large">Large</option>
                  <option value="X-Large">X-Large</option>
                </select>
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Player Number:
                <input
                  type="number"
                  name="playerNumber"
                  value={soccerFormData.playerNumber}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Current Roster(s) (comma-separated):
                <input
                  type="text"
                  name="currentRosters"
                  placeholder="e.g. The Gizmos, Team B"
                  value={soccerFormData.currentRosters}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Roster Jerseys Owned (comma-separated):
                <input
                  type="text"
                  name="rosterJerseysOwned"
                  placeholder="e.g. The Gizmos (Red)"
                  value={soccerFormData.rosterJerseysOwned}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Comments:
                <textarea
                  name="comments"
                  value={soccerFormData.comments}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px', minHeight: '60px' }}
                />
              </label>
              <div style={{ display: 'flex', gap: '10px' }}>
                <button type="submit" style={{
                  padding: '10px 20px', backgroundColor: '#61dafb', border: 'none',
                  cursor: 'pointer', fontSize: '16px'
                }}>
                  Save Info
                </button>
                <button
                  type="button"
                  onClick={() => setIsEditingSoccer(false)}
                  style={{
                    padding: '10px 20px', backgroundColor: '#555', border: 'none',
                    color: 'white', cursor: 'pointer', fontSize: '16px'
                  }}>
                  Cancel
                </button>
              </div>
            </form>
          );
        }
        if (soccerDetails) {
          return (
            // --- NEW: This is the updated "Sports Info" view ---
            <div style={{ textAlign: 'left', maxWidth: '500px' }}>
              <h2>My Soccer Details</h2>

              {/* NEW: Replaced <p> tags with our new table layout */}
              <div className="info-table">
                <div className="info-label">Favored Position:</div>
                <div className="info-value">{soccerDetails.favoredPosition}</div>

                <div className="info-label">Jersey Size:</div>
                <div className="info-value">{soccerDetails.jerseySize}</div>

                <div className="info-label">Player Number:</div>
                <div className="info-value">{soccerDetails.playerNumber}</div>

                <div className="info-label">Current Rosters:</div>
                <div className="info-value">{soccerDetails.currentRosters.join(', ')}</div>

                <div className="info-label">Jerseys Owned:</div>
                <div className="info-value">{soccerDetails.rosterJerseysOwned.join(', ')}</div>

                <div className="info-label">Comments:</div>
                <div className="info-value">{soccerDetails.comments}</div>
              </div>

              <button
                onClick={() => {
                  setSoccerFormData({
                    ...soccerDetails,
                    currentRosters: soccerDetails.currentRosters.join(', '),
                    rosterJerseysOwned: soccerDetails.rosterJerseysOwned.join(', ')
                  });
                  setIsEditingSoccer(true);
                }}
                style={{
                  padding: '10px 20px', backgroundColor: '#61dafb', border: 'none',
                  cursor: 'pointer', fontSize: '16px', marginTop: '30px'
                }}>
                Edit Info
              </button>
            </div>
          );
        }
        // This "add info" prompt is unchanged
        return (
          <div style={{ textAlign: 'left' }}>
            <h2>Sports Information</h2>
            <p>You haven't added any sports information yet.</p>
            <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginTop: '20px' }}>
              <select style={{ padding: '8px' }}>
                <option value="soccer">Soccer</option>
              </select>
              <button
                onClick={() => {
                  setSoccerFormData({
                    favoredPosition: "", jerseySize: "Large", playerNumber: 0,
                    comments: "", currentRosters: "", rosterJerseysOwned: ""
                  });
                  setIsEditingSoccer(true);
                }}
                style={{
                  padding: '10px 20px', backgroundColor: '#61dafb', border: 'none',
                  cursor: 'pointer', fontSize: '16px'
                }}>
                Enter Info
              </button>
            </div>
          </div>
        );
      default:
        return <div><p>Page not found.</p></div>;
    }
  };

  /* --- The HTML (JSX) to Display --- */
  // (This section is unchanged)
  if (isLoading) {
    return (
      <div className="App">
        <header className="App-header">
          <h2>Loading...</h2>
        </header>
      </div>
    );
  }

  return (
    <div className="App">

      {/* --- Re-authentication Modal --- */}
      {needsReauth && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
          backgroundColor: 'rgba(0, 0, 0, 0.7)',
          display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000
        }}>
          <form
            onSubmit={handleReauthSubmit}
            style={{
              display: 'flex', flexDirection: 'column', gap: '15px',
              backgroundColor: '#282c34', padding: '30px', borderRadius: '8px',
              border: '1px solid #61dafb'
            }}
          >
            <h3 style={{ color: 'white', marginTop: 0 }}>Please Confirm Your Identity</h3>
            <p style={{ color: 'white', margin: 0 }}>To change your email, please enter your password.</p>
            <input
              type="password"
              placeholder="Enter your password"
              value={reauthPassword}
              onChange={(e) => setReauthPassword(e.target.value)}
              style={{ padding: '8px' }}
              required
            />
            <div style={{ display: 'flex', gap: '10px' }}>
              <button type="submit" style={{
                padding: '10px 20px', backgroundColor: '#61dafb', border: 'none',
                cursor: 'pointer', fontSize: '16px', flex: 1
              }}>
                Confirm
              </button>
              <button
                type="button"
                onClick={() => {
                  setNeedsReauth(false);
                  setReauthPassword("");
                }}
                style={{
                  padding: '10px 20px', backgroundColor: '#555', border: 'none',
                  color: 'white', cursor: 'pointer', fontSize: '16px', flex: 1
                }}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
      {/* --- END: Re-authentication Modal --- */}

      {loggedInUser ? (

        /* --- START: MAIN APP LAYOUT (USER IS LOGGED IN) --- */
        <div
          style={{
            display: 'flex', width: '100vw', height: '100vh',
            backgroundColor: '#282c34',
            flexDirection: isMobile ? 'column' : 'row'
          }}
        >

          {/* --- Mobile-Only Header --- */}
          {isMobile && (
            <header className="mobile-header">
              <h1>Team App</h1>
            </header>
          )}

          {/* --- Navigation Sidebar / Tab Bar --- */}
          <nav
            className={isMobile ? 'tab-bar' : 'sidebar'}
          >
            {!isMobile && <h3 style={{ marginTop: 0 }}>Team App</h3>}
            <button onClick={() => { setActiveView('profile'); }}
              className={activeView === 'profile' ? 'active' : ''}>
              My Profile
            </button>
            <button onClick={() => { setActiveView('sports'); }}
              className={activeView === 'sports' ? 'active' : ''}>
              Sports Info
            </button>
            <button onClick={handleSignOut} className="sign-out-button">
              Sign Out
            </button>
          </nav>

          {/* --- Main Content Area --- */}
          <main className={isMobile ? 'main-content mobile' : 'main-content'}>
            {renderActiveView()}
          </main>

        </div>
        /* --- END: MAIN APP LAYOUT --- */

      ) : (

        /* --- START: SIGN-UP / SIGN-IN FORM (USER IS LOGGED OUT) --- */
        <header className="App-header">

          {authView === 'signUp' ? (

            <form onSubmit={handleSignUp} style={{
              display: 'flex', flexDirection: 'column', gap: '15px',
              backgroundColor: '#282c34', padding: '20px',
              borderRadius: '8px', width: '350px'
            }}>
              <h2 style={{ margin: 0, marginBottom: '10px' }}>Create Your Account</h2>
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Player Name:
                <input
                  type="text"
                  placeholder="Your full name"
                  value={playerName}
                  onChange={(e) => setPlayerName(e.target.value)}
                  required
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Email:
                <input
                  type="email"
                  placeholder="you@email.com"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Password (min 6 characters):
                <input
                  type="password"
                  placeholder="Choose a password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Phone:
                <input
                  type="tel"
                  placeholder="360-555-1234"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Address:
                <input
                  type="text"
                  placeholder="123 Main St"
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Notification Preference:
                <select
                  value={notificationPreference}
                  onChange={(e) => setNotificationPreference(e.target.value)}
                  style={{ padding: '8px' }}
                >
                  <option value="Email">Email</option>
                  <option value="Text Message">Text Message</option>
                </select>
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Comments:
                <textarea
                  value={comments}
                  onChange={(e) => setComments(e.target.value)}
                  placeholder="Feel free to contact me anytime."
                  style={{ padding: '8px', minHeight: '60px' }}
                />
              </label>
              <button type="submit" style={{
                padding: '10px', backgroundColor: '#61dafb', border: 'none',
                cursor: 'pointer', fontSize: '16px', fontWeight: 'bold'
              }}>
                Sign Up
              </button>
              <p style={{ fontSize: '14px', marginTop: '15px', marginBottom: '0' }}>
                Already have an account?{' '}
                <button
                  type="button"
                  onClick={() => setAuthView('signIn')}
                  style={{
                    background: 'none', border: 'none', color: '#61dafb',
                    cursor: 'pointer', padding: 0, fontSize: '14px'
                  }}
                >
                  Sign In
                </button>
              </p>
            </form>

          ) : (

            <form onSubmit={handleSignIn} style={{
              display: 'flex', flexDirection: 'column', gap: '15px',
              backgroundColor: '#282c34', padding: '20px',
              borderRadius: '8px', width: '350px'
            }}>
              <h2 style={{ margin: 0, marginBottom: '10px' }}>Welcome Back!</h2>
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Email:
                <input
                  type="email"
                  placeholder="you@email.com"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  style={{ padding: '8px' }}
                />
              </label>
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Password:
                <input
                  type="password"
                  placeholder="Your password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  style={{ padding: '8px' }}
                />
              </label>
              <button type="submit" style={{
                padding: '10px', backgroundColor: '#61dafb', border: 'none',
                cursor: 'pointer', fontSize: '16px', fontWeight: 'bold'
              }}>
                Sign In
              </button>
              <p style={{ fontSize: '14px', marginTop: '15px', marginBottom: '0' }}>
                Don't have an account?{' '}
                <button
                  type="button"
                  onClick={() => setAuthView('signUp')}
                  style={{
                    background: 'none', border: 'none', color: '#61dafb',
                    cursor: 'pointer', padding: 0, fontSize: '14px'
                  }}
                >
                  Sign Up
                </button>
              </p>
            </form>
          )}

        </header>
      )}

    </div>
  );
}

export default App;
