import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

function ManagerDashboard() {
  // Get functions from the brain
  const { 
    fetchRosters, createRoster, deleteRoster, 
    addPlayerToRoster, removePlayerFromRoster,
    createEvent, fetchEvents, deleteEvent
  } = useAuth();

  // Global state for this component
  const [rosters, setRosters] = useState([]);
  const [isLoadingRosters, setIsLoadingRosters] = useState(true);
  const [selectedRoster, setSelectedRoster] = useState(null);

  // Create Roster Form State
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newRosterName, setNewRosterName] = useState("");
  const [newRosterSeason, setNewRosterSeason] = useState("");
  const [newRosterCapacity, setNewRosterCapacity] = useState("20");

  // Add Player State
  const [inviteEmail, setInviteEmail] = useState("");

  // NEW: Event State
  const [rosterEvents, setRosterEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({
    type: "Game", // or Practice
    dateTime: "",
    location: "",
    opponent: ""
  });

  useEffect(() => {
    loadRosters();
  }, []);

  // NEW: Load events when a roster is selected
  useEffect(() => {
    if (selectedRoster) {
      loadEvents(selectedRoster.id);
    }
  }, [selectedRoster]);

  const loadRosters = async () => {
    setIsLoadingRosters(true);
    const data = await fetchRosters();
    setRosters(data);
    setIsLoadingRosters(false);
    if (selectedRoster) {
      const updatedRoster = data.find(r => r.id === selectedRoster.id);
      if (updatedRoster) setSelectedRoster(updatedRoster);
    }
  };

  const loadEvents = async (rosterId) => {
    const events = await fetchEvents(rosterId);
    setRosterEvents(events);
  };

  // --- Roster Actions ---

  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    if (!newRosterName || !newRosterSeason) {
      alert("Please fill in all fields");
      return;
    }
    const success = await createRoster(newRosterName, newRosterSeason, newRosterCapacity);
    if (success) {
      alert("Roster created!");
      setShowCreateForm(false);
      setNewRosterName("");
      setNewRosterSeason("");
      setNewRosterCapacity("20");
      loadRosters();
    }
  };

  const handleDeleteRoster = async (rosterId) => {
    if (window.confirm("Delete this roster? This cannot be undone.")) {
      const success = await deleteRoster(rosterId);
      if (success) loadRosters();
    }
  };

  const handleAddPlayer = async (e) => {
    e.preventDefault();
    if (!inviteEmail) return;

    const success = await addPlayerToRoster(selectedRoster.id, inviteEmail);
    if (success) {
      alert("Player added!");
      setInviteEmail("");
      loadRosters();
    }
  };

  const handleRemovePlayer = async (playerSummary) => {
    if (window.confirm(`Remove ${playerSummary.playerName} from this roster?`)) {
      const success = await removePlayerFromRoster(selectedRoster.id, playerSummary);
      if (success) loadRosters();
    }
  };

  // --- NEW: Event Actions ---

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    if (!newEvent.dateTime || !newEvent.location) {
      alert("Date/Time and Location are required.");
      return;
    }

    const success = await createEvent(selectedRoster.id, newEvent);
    if (success) {
      alert("Event created!");
      setNewEvent({ type: "Game", dateTime: "", location: "", opponent: "" }); // Reset form
      loadEvents(selectedRoster.id); // Refresh list
    }
  };

  const handleDeleteEvent = async (eventId) => {
    if (window.confirm("Delete this event?")) {
      const success = await deleteEvent(selectedRoster.id, eventId);
      if (success) loadEvents(selectedRoster.id);
    }
  };


  // --- VIEW 1: Roster Detail View ---
  if (selectedRoster) {
    return (
      <div style={{ textAlign: 'left', maxWidth: '1000px' }}>
        <button 
          onClick={() => setSelectedRoster(null)}
          style={{ marginBottom: '20px', background: 'none', border: 'none', color: '#61dafb', cursor: 'pointer', fontSize: '16px' }}
        >
          ← Back to Dashboard
        </button>

        <h2 style={{ margin: '0 0 10px 0' }}>{selectedRoster.name}</h2>
        <p style={{ color: '#ccc', marginTop: 0 }}>
          {selectedRoster.season} • {selectedRoster.players?.length || 0} / {selectedRoster.maxCapacity} Players
        </p>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '30px', marginTop: '30px' }}>

          {/* COL 1: Player Management */}
          <div>
            <div style={{ backgroundColor: '#282c34', padding: '20px', borderRadius: '8px', marginBottom: '20px', border: '1px solid #444' }}>
              <h3 style={{ margin: '0 0 15px 0', borderBottom: '1px solid #444', paddingBottom: '10px' }}>Roster</h3>
              {(!selectedRoster.players || selectedRoster.players.length === 0) ? (
                <p style={{ fontStyle: 'italic', color: '#888' }}>No players yet.</p>
              ) : (
                <ul style={{ listStyle: 'none', padding: 0 }}>
                  {selectedRoster.players.map((player, index) => (
                    <li key={index} style={{ 
                      display: 'flex', justifyContent: 'space-between', alignItems: 'center',
                      padding: '10px', backgroundColor: '#333', marginBottom: '5px', borderRadius: '5px'
                    }}>
                      <div>
                        <span style={{ fontWeight: 'bold', display: 'block' }}>{player.playerName}</span>
                        <span style={{ fontSize: '12px', color: '#aaa' }}>{player.email}</span>
                      </div>
                      <button 
                        onClick={() => handleRemovePlayer(player)}
                        style={{ color: '#ff6b6b', background: 'none', border: 'none', cursor: 'pointer' }}
                      >
                        Remove
                      </button>
                    </li>
                  ))}
                </ul>
              )}

              <form onSubmit={handleAddPlayer} style={{ marginTop: '20px', borderTop: '1px solid #444', paddingTop: '15px' }}>
                <p style={{ marginTop: 0, fontSize: '14px', fontWeight: 'bold' }}>Add Player</p>
                <div style={{ display: 'flex', gap: '10px' }}>
                  <input 
                    type="email" 
                    placeholder="player@email.com"
                    value={inviteEmail}
                    onChange={(e) => setInviteEmail(e.target.value)}
                    style={{ flex: 1, padding: '8px', boxSizing: 'border-box' }}
                  />
                  <button type="submit" style={{
                    padding: '8px 15px', backgroundColor: '#61dafb',
                    border: 'none', cursor: 'pointer', fontWeight: 'bold', borderRadius: '4px'
                  }}>
                    Add
                  </button>
                </div>
              </form>
            </div>
          </div>

          {/* COL 2: Event Management (NEW) */}
          <div>
            <div style={{ backgroundColor: '#282c34', padding: '20px', borderRadius: '8px', border: '1px solid #444' }}>
              <h3 style={{ margin: '0 0 15px 0', borderBottom: '1px solid #444', paddingBottom: '10px' }}>Schedule</h3>

              {/* List Events */}
              <div style={{ maxHeight: '300px', overflowY: 'auto', marginBottom: '20px' }}>
                {rosterEvents.length === 0 ? (
                  <p style={{ fontStyle: 'italic', color: '#888' }}>No events scheduled.</p>
                ) : (
                  rosterEvents.map(event => (
                    <div key={event.id} style={{ 
                      backgroundColor: '#333', padding: '10px', marginBottom: '8px', borderRadius: '5px',
                      display: 'flex', justifyContent: 'space-between', alignItems: 'center'
                    }}>
                      <div>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                          <span style={{ 
                            backgroundColor: event.type === 'Game' ? '#4caf50' : '#2196f3', 
                            fontSize: '10px', padding: '2px 6px', borderRadius: '4px', fontWeight: 'bold', color: 'white'
                          }}>
                            {event.type}
                          </span>
                          <span style={{ fontWeight: 'bold' }}>{event.dateTime}</span>
                        </div>
                        <div style={{ fontSize: '13px', color: '#ccc', marginTop: '4px' }}>
                           @ {event.location} {event.opponent && `vs ${event.opponent}`}
                        </div>
                      </div>
                      <button 
                        onClick={() => handleDeleteEvent(event.id)}
                        style={{ color: '#ff6b6b', background: 'none', border: 'none', cursor: 'pointer' }}
                      >
                        ✕
                      </button>
                    </div>
                  ))
                )}
              </div>

              {/* Create Event Form */}
              <form onSubmit={handleCreateEvent} style={{ borderTop: '1px solid #444', paddingTop: '15px' }}>
                <p style={{ marginTop: 0, fontSize: '14px', fontWeight: 'bold' }}>Schedule New Event</p>

                <div style={{ display: 'grid', gap: '10px' }}>
                  <select 
                    value={newEvent.type}
                    onChange={(e) => setNewEvent({...newEvent, type: e.target.value})}
                    style={{ padding: '8px' }}
                  >
                    <option value="Game">Game</option>
                    <option value="Practice">Practice</option>
                    <option value="Social">Social</option>
                  </select>

                  <input 
                    type="datetime-local" 
                    value={newEvent.dateTime}
                    onChange={(e) => setNewEvent({...newEvent, dateTime: e.target.value})}
                    style={{ padding: '8px' }}
                  />

                  <input 
                    type="text" 
                    placeholder="Location"
                    value={newEvent.location}
                    onChange={(e) => setNewEvent({...newEvent, location: e.target.value})}
                    style={{ padding: '8px' }}
                  />

                  {newEvent.type === 'Game' && (
                    <input 
                      type="text" 
                      placeholder="Opponent Name"
                      value={newEvent.opponent}
                      onChange={(e) => setNewEvent({...newEvent, opponent: e.target.value})}
                      style={{ padding: '8px' }}
                    />
                  )}

                  <button type="submit" style={{
                    padding: '10px', backgroundColor: '#61dafb',
                    border: 'none', cursor: 'pointer', fontWeight: 'bold', borderRadius: '4px'
                  }}>
                    Create Event
                  </button>
                </div>
              </form>

            </div>
          </div>

        </div>
      </div>
    );
  }

  // --- VIEW 2: Roster List View (Default) ---
  return (
    <div style={{ textAlign: 'left', maxWidth: '800px' }}>
      <h2>Manager Dashboard</h2>
      <p>Manage your teams and rosters here.</p>

      <div style={{ marginTop: '30px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h3>Current Rosters</h3>
          <button 
            onClick={() => setShowCreateForm(!showCreateForm)}
            style={{
              padding: '10px 15px', backgroundColor: '#61dafb', border: 'none',
              cursor: 'pointer', fontSize: '14px', fontWeight: 'bold', borderRadius: '5px'
            }}
          >
            {showCreateForm ? "Cancel" : "+ Create New Roster"}
          </button>
        </div>

        {showCreateForm && (
          <form onSubmit={handleCreateSubmit} style={{
            backgroundColor: '#3a3f4a', padding: '20px', borderRadius: '8px', marginBottom: '20px',
            border: '1px solid #61dafb'
          }}>
            <h4 style={{ marginTop: 0, color: 'white' }}>New Roster Details</h4>
            <div style={{ display: 'flex', gap: '15px', flexWrap: 'wrap' }}>
              <label style={{ flex: 1, minWidth: '200px' }}>
                <span style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>Roster Name</span>
                <input type="text" placeholder="e.g. The Gizmos" value={newRosterName} onChange={(e) => setNewRosterName(e.target.value)} style={{ width: '100%', padding: '8px' }} />
              </label>
              <label style={{ flex: 1, minWidth: '150px' }}>
                <span style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>Season</span>
                <input type="text" placeholder="e.g. Fall 2025" value={newRosterSeason} onChange={(e) => setNewRosterSeason(e.target.value)} style={{ width: '100%', padding: '8px' }} />
              </label>
              <label style={{ width: '100px' }}>
                <span style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>Capacity</span>
                <input type="number" value={newRosterCapacity} onChange={(e) => setNewRosterCapacity(e.target.value)} style={{ width: '100%', padding: '8px' }} />
              </label>
            </div>
            <button type="submit" style={{ marginTop: '15px', padding: '10px 20px', backgroundColor: '#61dafb', border: 'none', cursor: 'pointer', fontSize: '14px', fontWeight: 'bold' }}>Save Roster</button>
          </form>
        )}

        {isLoadingRosters ? (
          <p>Loading rosters...</p>
        ) : rosters.length === 0 ? (
          <p style={{ color: '#aaa', fontStyle: 'italic' }}>No rosters found.</p>
        ) : (
          <div style={{ display: 'grid', gap: '15px' }}>
            {rosters.map(roster => (
              <div key={roster.id} style={{
                backgroundColor: '#282c34', padding: '15px', borderRadius: '8px',
                border: '1px solid #444', display: 'flex', justifyContent: 'space-between', alignItems: 'center'
              }}>
                <div>
                  <h4 style={{ margin: 0, color: '#61dafb' }}>{roster.name}</h4>
                  <p style={{ margin: '5px 0 0 0', fontSize: '14px', color: '#ccc' }}>
                    {roster.season} • {roster.players?.length || 0} / {roster.maxCapacity} players
                  </p>
                </div>
                <div>
                  <button 
                    onClick={() => setSelectedRoster(roster)}
                    style={{ marginRight: '10px', padding: '5px 15px', backgroundColor: '#555', border: 'none', cursor: 'pointer', fontSize: '12px', color: 'white', borderRadius: '4px' }}
                  >
                    Manage
                  </button>
                  <button 
                    onClick={() => handleDeleteRoster(roster.id)}
                    style={{ padding: '5px 10px', backgroundColor: '#ff6b6b', border: 'none', cursor: 'pointer', fontSize: '12px', color: 'white', borderRadius: '4px' }}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default ManagerDashboard;
