/* --- React and Firebase Imports --- */
import React, { useState, useEffect } from 'react'; 
import './App.css';
// NEW: Added EmailAuthProvider and reauthenticateWithCredential
import { 
  createUserWithEmailAndPassword, 
  signOut, 
  signInWithEmailAndPassword, 
  onAuthStateChanged, 
  updateEmail,
  EmailAuthProvider,
  reauthenticateWithCredential
} from "firebase/auth";
import { doc, setDoc, getDoc, updateDoc } from "firebase/firestore"; 
import { auth, db } from "./firebase"; 

/* --- The Main App Component --- */
function App() {

  /* --- State Variables --- */

  const [loggedInUser, setLoggedInUser] = useState(null);
  const [activeView, setActiveView] = useState('profile');
  const [authView, setAuthView] = useState('signUp'); 
  const [isLoading, setIsLoading] = useState(true);

  // --- Form State Variables (for sign-up) ---
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [playerName, setPlayerName] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [notificationPreference, setNotificationPreference] = useState("Email"); 
  const [comments, setComments] = useState("");

  // --- NEW: State Variables for Profile Editing ---
  const [isEditingProfile, setIsEditingProfile] = useState(false);
  const [profileFormData, setProfileFormData] = useState({});

  // --- NEW: State for re-authentication modal ---
  const [needsReauth, setNeedsReauth] = useState(false);
  const [reauthPassword, setReauthPassword] = useState("");

  // --- NEW: State Variables for Sports Info ---

  // This holds the *saved* soccer data from Firestore
  const [soccerDetails, setSoccerDetails] = useState(null); 

  // This holds the *draft* data as the user types in the form
  const [soccerFormData, setSoccerFormData] = useState({
    favoredPosition: "",
    jerseySize: "Large",
    playerNumber: 0,
    comments: "",
    currentRosters: "", // We'll use a single string, then save as an array
    rosterJerseysOwned: "" // We'll use a single string, then save as an array
  });

  // This toggles the sports form on/off
  const [isEditingSoccer, setIsEditingSoccer] = useState(false);


  /* --- Check for logged-in user on app load --- */
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        // --- User is logged in ---

        // 1. Fetch the User Profile
        const userDocRef = doc(db, "users", user.uid);
        const userDoc = await getDoc(userDocRef);

        // 2. NEW: Fetch the Soccer Details
        // We create a reference to the 'soccer' doc inside the 'sportsDetails' sub-collection
        const soccerDocRef = doc(db, "users", user.uid, "sportsDetails", "soccer");
        const soccerDoc = await getDoc(soccerDocRef);

        if (userDoc.exists()) {
          setLoggedInUser(userDoc.data());
          setActiveView('profile'); 
        } else {
          console.error("No user profile found in Firestore!");
          setLoggedInUser(null);
        }

        // NEW: Check if the soccer doc exists
        if (soccerDoc.exists()) {
          setSoccerDetails(soccerDoc.data()); // Save the data
        } else {
          setSoccerDetails(null); // No data found
        }

      } else {
        // --- User is logged out ---
        setLoggedInUser(null);
        setSoccerDetails(null); // Clear soccer data on logout
      }
      setIsLoading(false);
    });

    return () => unsubscribe();
  }, []); // The empty array [] means this effect runs only ONCE.

  /* --- The Sign-Up Function --- */
  const handleSignUp = async (e) => {
    // ... (This function remains unchanged) ...
    e.preventDefault(); 
    if (password.length < 6) {
      alert("Password must be at least 6 characters long.");
      return; 
    }
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      const userProfileData = {
        uid: user.uid,
        playerName: playerName,
        email: email, 
        phone: phone,
        address: address,
        notificationPreference: notificationPreference,
        comments: comments,
        isAdmin: false
      };
      await setDoc(doc(db, "users", user.uid), userProfileData);
      setLoggedInUser(userProfileData);
      setActiveView('profile');
      setEmail("");
      setPassword("");
      setPlayerName("");
      setPhone("");
      setAddress("");
      setNotificationPreference("Email");
      setComments("");
    } catch (error) {
      console.error("Error signing up:", error);
      alert("Error: " + error.message);
    }
  };

  // NEW: The Sign-In Function
  const handleSignIn = async (e) => {
    e.preventDefault();
    setIsLoading(true); // Show loading spinner
    try {
      // 1. Try to sign in the user
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // 2. Fetch their user profile from Firestore
      const userDocRef = doc(db, "users", user.uid);
      const userDoc = await getDoc(userDocRef);

      // We also need to fetch soccer details on sign-in
      const soccerDocRef = doc(db, "users", user.uid, "sportsDetails", "soccer");
      const soccerDoc = await getDoc(soccerDocRef);
      if (soccerDoc.exists()) {
        setSoccerDetails(soccerDoc.data());
      } else {
        setSoccerDetails(null);
      }

      if (userDoc.exists()) {
        // 3. Set the logged-in user state
        setLoggedInUser(userDoc.data());
        setActiveView('profile'); // Go to profile page
      } else {
        // This case is rare, but good to handle
        alert("Authentication successful, but no profile was found.");
        setLoggedInUser(null);
      }

      // Clear forms
      setEmail("");
      setPassword("");

    } catch (error) {
      console.error("Error signing in:", error);
      alert("Error: " + error.message);
    }
    setIsLoading(false); // Hide loading spinner
  };

  // NEW: The Sign-Out Function
  const handleSignOut = async () => {
    try {
      await signOut(auth); // Tell Firebase to sign the user out
      setLoggedInUser(null); // Clear the user from our state
      setSoccerDetails(null); // Clear sports data
      setActiveView('profile'); // Reset view to default
      setAuthView('signIn'); // Set auth form to 'signIn'
    } catch (error) {
      console.error("Error signing out:", error);
      alert("Error: " + error.message);
    }
  };


  // NEW: Helper function to handle changes in the profile edit form
  const handleProfileFormChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  // NEW: The Profile Edit Submit Function
  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    if (!loggedInUser) return;

    // We'll update Auth and Firestore in two separate steps.

    // --- Step 1: Update Firebase Authentication (Email) ---
    // Check if the email in the form is different from the logged-in user's email
    if (profileFormData.email !== loggedInUser.email) {
      try {
        await updateEmail(auth.currentUser, profileFormData.email);
        console.log("Email updated in Firebase Auth!");
      } catch (error) {
        console.error("Error updating email:", error);

        // --- NEW: Check for the specific re-auth error ---
        if (error.code === 'auth/requires-recent-login') {
          // If this error happens, open our re-auth modal
          alert("This is a sensitive operation. Please re-enter your password to confirm the change.");
          setNeedsReauth(true);
        } else {
          // For any other error (like 'email-already-in-use')
          alert("Error: " + error.message);
        }
        // Stop the function here
        return;
      }
    }

    // --- Step 2: Update Firestore Database (The rest of the profile) ---
    try {
      // Create an object with only the fields we want to update.
      // We explicitly leave out 'isAdmin' so the user cannot change it.
      const dataToUpdate = {
        playerName: profileFormData.playerName,
        phone: profileFormData.phone,
        address: profileFormData.address,
        notificationPreference: profileFormData.notificationPreference,
        comments: profileFormData.comments,
        email: profileFormData.email // We also update the email in Firestore
      };

      // Get the reference to the user's document
      const userDocRef = doc(db, "users", loggedInUser.uid);

      // Use 'updateDoc' to update the fields
      await updateDoc(userDocRef, dataToUpdate);

      // --- Step 3: Update Local State ---
      // Update the main loggedInUser state with the new data
      setLoggedInUser(prevUser => ({
        ...prevUser,
        ...dataToUpdate 
      }));

      // Turn off edit mode
      setIsEditingProfile(false);
      alert("Profile successfully updated!");

    } catch (error) {
      console.error("Error updating profile in Firestore:", error);
      alert("Error saving profile: " + error.message);
    }
  };

  // --- NEW: Function to handle the re-authentication ---
  const handleReauthSubmit = async (e) => {
    e.preventDefault();
    if (!auth.currentUser || !reauthPassword) return;

    try {
      // 1. Get the user's credential
      const credential = EmailAuthProvider.credential(
        auth.currentUser.email,
        reauthPassword
      );

      // 2. Re-authenticate the user
      await reauthenticateWithCredential(auth.currentUser, credential);

      console.log("User re-authenticated!");

      // 3. --- Retry the email update ---
      // We use profileFormData.email because that's what's in the form
      await updateEmail(auth.currentUser, profileFormData.email);
      console.log("Email update successful after re-auth!");

      // 4. Update the email in our Firestore 'users' doc as well
      const userDocRef = doc(db, "users", loggedInUser.uid);
      await updateDoc(userDocRef, {
        email: profileFormData.email
      });

      // 5. Update our local state
      setLoggedInUser(prevUser => ({
        ...prevUser,
        email: profileFormData.email
      }));

      // 6. Close the modal and reset fields
      alert("Email successfully updated!");
      setNeedsReauth(false);
      setReauthPassword("");
      setIsEditingProfile(false); // Close the edit profile form too

    } catch (error) {
      console.error("Error during re-authentication:", error);
      alert("Error: Incorrect password or another error occurred. " + error.message);
    }
  };


  /* --- NEW: The Soccer Form Submit Function --- */
  const handleSoccerSubmit = async (e) => {
    e.preventDefault();
    if (!loggedInUser) {
      alert("You must be logged in to save data.");
      return;
    }

    try {
      // Create the data object to save
      const soccerDataToSave = {
        ...soccerFormData,
        // Convert the comma-separated strings into arrays
        currentRosters: soccerFormData.currentRosters.split(',').map(item => item.trim()),
        rosterJerseysOwned: soccerFormData.rosterJerseysOwned.split(',').map(item => item.trim()),
        // Ensure player number is saved as a number
        playerNumber: Number(soccerFormData.playerNumber) || 0,
      };

      // Create a reference to the document:
      // users/{USER_ID}/sportsDetails/soccer
      const soccerDocRef = doc(db, "users", loggedInUser.uid, "sportsDetails", "soccer");

      // Save the document
      await setDoc(soccerDocRef, soccerDataToSave);

      // Update our local state to show the new data
      setSoccerDetails(soccerDataToSave); 
      // Hide the form
      setIsEditingSoccer(false); 

      alert("Soccer info saved!");

    } catch (error) {
      console.error("Error saving soccer details:", error);
      alert("Error: " + error.message);
    }
  };

  // NEW: Helper function to handle changes in the soccer form
  const handleSoccerFormChange = (e) => {
    // This is a standard React pattern for handling forms.
    // It updates the 'soccerFormData' state object.
    const { name, value } = e.target;
    setSoccerFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };


  /* --- Helper Function to Render Main Content --- */
  const renderActiveView = () => {
    switch (activeView) {
      case 'profile':
        // NEW: This logic is now similar to the 'sports' view

        // 1. If we are in "Edit Mode", show the profile form
        if (isEditingProfile) {
          return (
            /* --- START: PROFILE EDIT FORM --- */
            <form onSubmit={handleProfileSubmit} style={{ 
              display: 'flex', flexDirection: 'column', gap: '15px', 
              maxWidth: '400px', textAlign: 'left'
            }}>
              <h2>Edit My Profile</h2>

              {/* Player Name Field */}
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Player Name:
                <input 
                  type="text"
                  name="playerName"
                  value={profileFormData.playerName}
                  onChange={handleProfileFormChange}
                  required
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Email Field */}
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Email:
                <input 
                  type="email"
                  name="email"
                  value={profileFormData.email}
                  onChange={handleProfileFormChange}
                  required
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Phone Field */}
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Phone:
                <input 
                  type="tel"
                  name="phone"
                  value={profileFormData.phone}
                  onChange={handleProfileFormChange}
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Address Field */}
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Address:
                <input 
                  type="text"
                  name="address"
                  value={profileFormData.address}
                  onChange={handleProfileFormChange}
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Notification Preference Field */}
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Notification Preference:
                <select 
                  name="notificationPreference"
                  value={profileFormData.notificationPreference} 
                  onChange={handleProfileFormChange}
                  style={{ padding: '8px' }}
                >
                  <option value="Email">Email</option>
                  <option value="Text Message">Text Message</option>
                </select>
              </label>

              {/* Comments Field */}
              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Comments:
                <textarea 
                  name="comments"
                  value={profileFormData.comments}
                  onChange={handleProfileFormChange}
                  placeholder="Feel free to contact me anytime."
                  style={{ padding: '8px', minHeight: '60px' }}
                />
              </label>

              {/* isAdmin field is NOT shown, so it cannot be edited */}

              <div style={{ display: 'flex', gap: '10px' }}>
                <button type="submit" style={{ 
                  padding: '10px 20px', backgroundColor: '#61dafb', border: 'none', 
                  cursor: 'pointer', fontSize: '16px'
                }}>
                  Save Profile
                </button>
                <button 
                  type="button" 
                  onClick={() => setIsEditingProfile(false)} // Cancel button
                  style={{ 
                    padding: '10px 20px', backgroundColor: '#555', border: 'none', 
                    color: 'white', cursor: 'pointer', fontSize: '16px'
                }}>
                  Cancel
                </button>
              </div>
            </form>
            /* --- END: PROFILE EDIT FORM --- */
          );
        }

        // 2. If not in "Edit Mode", show the profile details
        return (
          <div style={{ textAlign: 'left' }}>
            <h2>My Profile</h2>
            <p><strong>Player Name:</strong> {loggedInUser.playerName}</p>
            <p><strong>Email:</strong> {loggedInUser.email}</p>
            <p><strong>Phone:</strong> {loggedInUser.phone}</p>
            <p><strong>Address:</strong> {loggedInUser.address}</p>
            <p><strong>Preference:</strong> {loggedInUser.notificationPreference}</p>
            <p><strong>Comments:</strong> {loggedInUser.comments}</p>
            <p><strong>Is Admin:</strong> {loggedInUser.isAdmin ? 'Yes' : 'No'}</p>

            {/* NEW: Edit Profile Button */}
            <button 
              onClick={() => {
                // Pre-fill the form with the current user data
                setProfileFormData(loggedInUser);
                // Turn on edit mode
                setIsEditingProfile(true);
              }} 
              style={{ 
                padding: '10px 20px', backgroundColor: '#61dafb', border: 'none', 
                cursor: 'pointer', fontSize: '16px', marginTop: '20px'
            }}>
              Edit Profile
            </button>
          </div>
        );

      case 'sports':
        // --- NEW: This is the new logic for the Sports Info page ---

        // 1. If we are in "Edit Mode", show the form
        if (isEditingSoccer) {
          return (
            /* --- START: SOCCER INFO FORM --- */
            <form onSubmit={handleSoccerSubmit} style={{ 
              display: 'flex', flexDirection: 'column', gap: '15px', 
              maxWidth: '400px', textAlign: 'left'
            }}>
              <h2>Enter Soccer Details</h2>

              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Favored Position:
                <input 
                  type="text"
                  name="favoredPosition" // 'name' must match the state key
                  value={soccerFormData.favoredPosition}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px' }}
                />
              </label>

              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Jersey Size:
                <select 
                  name="jerseySize"
                  value={soccerFormData.jerseySize}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px' }}
                >
                  <option value="Small">Small</option>
                  <option value="Medium">Medium</option>
                  <option value="Large">Large</option>
                  <option value="X-Large">X-Large</option>
                </select>
              </label>

              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Player Number:
                <input 
                  type="number"
                  name="playerNumber"
                  value={soccerFormData.playerNumber}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px' }}
                />
              </label>

              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Current Roster(s) (comma-separated):
                <input 
                  type="text"
                  name="currentRosters"
                  placeholder="e.g. The Gizmos, Team B"
                  value={soccerFormData.currentRosters}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px' }}
                />
              </label>

              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Roster Jerseys Owned (comma-separated):
                <input 
                  type="text"
                  name="rosterJerseysOwned"
                  placeholder="e.g. The Gizmos (Red)"
                  value={soccerFormData.rosterJerseysOwned}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px' }}
                />
              </label>

              <label style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                Comments:
                <textarea 
                  name="comments"
                  value={soccerFormData.comments}
                  onChange={handleSoccerFormChange}
                  style={{ padding: '8px', minHeight: '60px' }}
                />
              </label>

              <div style={{ display: 'flex', gap: '10px' }}>
                <button type="submit" style={{ 
                  padding: '10px 20px', backgroundColor: '#61dafb', border: 'none', 
                  cursor: 'pointer', fontSize: '16px'
                }}>
                  Save Info
                </button>
                <button 
                  type="button" 
                  onClick={() => setIsEditingSoccer(false)} // Cancel button
                  style={{ 
                    padding: '10px 20px', backgroundColor: '#555', border: 'none', 
                    color: 'white', cursor: 'pointer', fontSize: '16px'
                }}>
                  Cancel
                </button>
              </div>
            </form>
            /* --- END: SOCCER INFO FORM --- */
          );
        }

        // 2. If data EXISTS, show it
        if (soccerDetails) {
          return (
            <div style={{ textAlign: 'left' }}>
              <h2>My Soccer Details</h2>
              <p><strong>Favored Position:</strong> {soccerDetails.favoredPosition}</p>
              <p><strong>Jersey Size:</strong> {soccerDetails.jerseySize}</p>
              <p><strong>Player Number:</strong> {soccerDetails.playerNumber}</p>
              {/* We use .join() to display the arrays nicely */}
              <p><strong>Current Rosters:</strong> {soccerDetails.currentRosters.join(', ')}</p>
              <p><strong>Jerseys Owned:</strong> {soccerDetails.rosterJerseysOwned.join(', ')}</p>
              <p><strong>Comments:</strong> {soccerDetails.comments}</p>

              <button 
                onClick={() => {
                  // NEW: When editing, we pre-fill the form with existing data
                  // We convert arrays back to strings for the form
                  setSoccerFormData({
                    ...soccerDetails,
                    currentRosters: soccerDetails.currentRosters.join(', '),
                    rosterJerseysOwned: soccerDetails.rosterJerseysOwned.join(', ')
                  });
                  setIsEditingSoccer(true);
                }} 
                style={{ 
                  padding: '10px 20px', backgroundColor: '#61dafb', border: 'none', 
                  cursor: 'pointer', fontSize: '16px', marginTop: '20px'
              }}>
                Edit Info
              </button>
            </div>
          );
        }

        // 3. If NO data exists, show the "Add Info" prompt
        return (
          <div style={{ textAlign: 'left' }}>
            <h2>Sports Information</h2>
            <p>You haven't added any sports information yet.</p>
            <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginTop: '20px' }}>
              <select style={{ padding: '8px' }}>
                {/* Only one option for now, as requested */}
                <option value="soccer">Soccer</option>
              </select>
              <button 
                onClick={() => {
                  // NEW: Clear the form and go to edit mode
                  setSoccerFormData({
                    favoredPosition: "", jerseySize: "Large", playerNumber: 0,
                    comments: "", currentRosters: "", rosterJerseysOwned: ""
                  });
                  setIsEditingSoccer(true);
                }} 
                style={{ 
                  padding: '10px 20px', backgroundColor: '#61dafb', border: 'none', 
                  cursor: 'pointer', fontSize: '16px'
              }}>
                Enter Info
              </button>
            </div>
          </div>
        );

      // We don't need a 'logout' case, because the button calls handleSignOut directly.
      default:
        return <div><p>Page not found.</p></div>;
    }
  };


  /* --- The HTML (JSX) to Display --- */
  if (isLoading) {
    return (
      <div className="App">
        <header className="App-header">
          <h2>Loading...</h2>
        </header>
      </div>
    );
  }

  return (
    <div className="App">

      {/* --- NEW: Re-authentication Modal --- */}
      {/* This modal will pop up on top of everything else */}
      {needsReauth && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
          backgroundColor: 'rgba(0, 0, 0, 0.7)',
          display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000
        }}>
          <form 
            onSubmit={handleReauthSubmit} 
            style={{
              display: 'flex', flexDirection: 'column', gap: '15px',
              backgroundColor: '#282c34', padding: '30px', borderRadius: '8px',
              border: '1px solid #61dafb'
            }}
          >
            <h3 style={{ color: 'white', marginTop: 0 }}>Please Confirm Your Identity</h3>
            <p style={{ color: 'white', margin: 0 }}>To change your email, please enter your password.</p>
            <input
              type="password"
              placeholder="Enter your password"
              value={reauthPassword}
              onChange={(e) => setReauthPassword(e.target.value)}
              style={{ padding: '8px' }}
              required
            />
            <div style={{ display: 'flex', gap: '10px' }}>
              <button type="submit" style={{
                padding: '10px 20px', backgroundColor: '#61dafb', border: 'none',
                cursor: 'pointer', fontSize: '16px', flex: 1
              }}>
                Confirm
              </button>
              <button 
                type="button" 
                onClick={() => {
                  setNeedsReauth(false); // Just close the modal
                  setReauthPassword(""); // Clear the password
                }}
                style={{
                  padding: '10px 20px', backgroundColor: '#555', border: 'none',
                  color: 'white', cursor: 'pointer', fontSize: '16px', flex: 1
                }}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
      {/* --- END: Re-authentication Modal --- */}

      {loggedInUser ? (

        /* --- START: MAIN APP LAYOUT (USER IS LOGGED IN) --- */
        <div style={{ display: 'flex', width: '100vw', height: '100vh', backgroundColor: '#282c34' }}>

          {/* --- Navigation Sidebar --- */}
          <nav style={{ 
            width: '200px', 
            backgroundColor: '#1c1e22', 
            padding: '20px',
            color: 'white',
            display: 'flex',
            flexDirection: 'column',
            gap: '10px'
          }}>
            <h3 style={{ marginTop: 0 }}>Team App</h3>

            {/* Nav Button 1: Profile */}
            <button onClick={() => setActiveView('profile')} style={{
              padding: '10px',
              backgroundColor: activeView === 'profile' ? '#61dafb' : '#333',
              color: activeView === 'profile' ? '#000' : 'white',
              border: 'none',
              cursor: 'pointer',
              textAlign: 'left',
              fontSize: '16px'
            }}>
              My Profile
            </button>

            {/* Nav Button 2: Sports Info */}
            <button onClick={() => setActiveView('sports')} style={{
              padding: '10px',
              backgroundColor: activeView === 'sports' ? '#61dafb' : '#333',
              color: activeView === 'sports' ? '#000' : 'white',
              border: 'none',
              cursor: 'pointer',
              textAlign: 'left',
              fontSize: '16px'
            }}>
              Sports Info
            </button>

            {/* Nav Button 3: Sign Out */}
            <button onClick={handleSignOut} style={{ 
              padding: '10px', 
              backgroundColor: '#ff6b6b',
              color: 'white',
              border: 'none', 
              cursor: 'pointer',
              fontSize: '16px',
              textAlign: 'left',
              marginTop: 'auto' 
            }}>
              Sign Out
            </button>
          </nav>

          {/* --- Main Content Area --- */}
          <main style={{ 
            flex: 1, 
            padding: '40px', 
            color: 'white',
            overflowY: 'auto' 
          }}>
            {renderActiveView()}
          </main>
        </div>
        /* --- END: MAIN APP LAYM-OUT --- */

      ) : (

        /* --- START: SIGN-UP / SIGN-IN FORM (USER IS LOGGED OUT) --- */
        <header className="App-header">

          {authView === 'signUp' ? (

            /* --- START: SIGN-UP FORM --- */
            // STYLING FIX: I removed the duplicate/broken style properties
            <form onSubmit={handleSignUp} style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '15px',
              backgroundColor: '#282c34', 
              padding: '20px', 
              borderRadius: '8px',
              width: '350px' 
            }}>

              <h2 style={{ margin: 0, marginBottom: '10px' }}>Create Your Account</h2>

              {/* Player Name Field */}
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Player Name:
                <input 
                  type="text"
                  placeholder="Your full name"
                  value={playerName}
                  onChange={(e) => setPlayerName(e.target.value)} 
                  required
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Email Field */}
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Email:
                <input 
                  type="email"
                  placeholder="you@email.com"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)} 
                  required
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Password Field */}
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Password (min 6 characters):
                <input 
                  type="password"
                  placeholder="Choose a password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Phone Field */}
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Phone:
                <input 
                  type="tel"
                  placeholder="360-555-1234"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Address Field */}
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Address:
                <input 
                  type="text"
                  placeholder="123 Main St"
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Notification Preference Field */}
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Notification Preference:
                <select 
                  value={notificationPreference} 
                  onChange={(e) => setNotificationPreference(e.target.value)}
                  style={{ padding: '8px' }}
                >
                  <option value="Email">Email</option>
                  <option value="Text Message">Text Message</option>
                </select>
              </label>

              {/* Comments Field */}
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Comments:
                <textarea 
                  value={comments}
                  onChange={(e) => setComments(e.target.value)}
                  placeholder="Feel free to contact me anytime."
                  style={{ padding: '8px', minHeight: '60px' }}
                />
              </label>

              {/* Submit Button */}
              <button type="submit" style={{ 
                padding: '10px', 
                backgroundColor: '#61dafb', 
                border: 'none', 
                cursor: 'pointer',
                fontSize: '16px',
                fontWeight: 'bold'
              }}>
                Sign Up
              </button>

              {/* Toggle button to switch to Sign In */}
              <p style={{ fontSize: '14px', marginTop: '15px', marginBottom: '0' }}>
                Already have an account?{' '}
                <button 
                  type="button" 
                  onClick={() => setAuthView('signIn')} 
                  style={{
                    background: 'none',
                    border: 'none',
                    color: '#61dafb',
                    cursor: 'pointer',
                    padding: 0,
                    fontSize: '14px'
                  }}
                >
                  Sign In
                </button>
              </p>
            </form>
            /* --- END: SIGN-UP FORM --- */

          ) : (

            /* --- START: SIGN-IN FORM --- */
            <form onSubmit={handleSignIn} style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '15px',
              backgroundColor: '#282c34', 
              padding: '20px', 
              borderRadius: '8px',
              width: '350px' 
            }}>

              <h2 style={{ margin: 0, marginBottom: '10px' }}>Welcome Back!</h2>

              {/* Email Field */}
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Email:
                <input 
                  type="email"
                  placeholder="you@email.com"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)} 
                  required
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Password Field */}
              <label style={{ display: 'flex', flexDirection: 'column', textAlign: 'left', gap: '5px' }}>
                Password:
                <input 
                  type="password"
                  placeholder="Your password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  style={{ padding: '8px' }}
                />
              </label>

              {/* Submit Button */}
              <button type="submit" style={{ 
                padding: '10px', 
                backgroundColor: '#61dafb', 
                border: 'none', 
                cursor: 'pointer',
                fontSize: '16px',
                fontWeight: 'bold'
              }}>
                Sign In
              </button>

              {/* Toggle button to switch to Sign Up */}
              <p style={{ fontSize: '14px', marginTop: '15px', marginBottom: '0' }}>
                Don't have an account?{' '}
                <button 
                  type="button"
                  onClick={() => setAuthView('signUp')} 
                  style={{
                    background: 'none',
                    border: 'none',
                    color: '#61dafb',
                    cursor: 'pointer',
                    padding: 0,
                    fontSize: '14px'
                  }}
                >
                  Sign Up
                </button>
              </p>
            </form>
            /* --- END: SIGN-IN FORM --- */
          )}

        </header>
        /* --- END: SIGN-UP / SIGN-IN FORM (logged out) --- */
      )}

    </div>
  );
}

export default App;
