import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../context/AuthContext';
import { collection, query, orderBy, onSnapshot, limit } from "firebase/firestore";
import { db } from "../firebase";

function TeamChat() {
  const { fetchUserRosters, sendMessage, loggedInUser } = useAuth();

  // Data State
  const [myRosters, setMyRosters] = useState([]);
  const [selectedRosterId, setSelectedRosterId] = useState("");
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  // Helper to auto-scroll to bottom
  const messagesEndRef = useRef(null);

  // 1. Load the user's teams
  useEffect(() => {
    if (loggedInUser) {
      loadTeams();
    }
  }, [loggedInUser]);

  const loadTeams = async () => {
    const rosters = await fetchUserRosters(loggedInUser.uid);
    setMyRosters(rosters);
    if (rosters.length > 0) {
      setSelectedRosterId(rosters[0].id); // Default to first team
    }
  };

  // 2. LISTEN for messages (Real-Time!)
  useEffect(() => {
    if (!selectedRosterId) return;

    // Create a reference to the messages sub-collection
    const messagesRef = collection(db, "rosters", selectedRosterId, "messages");

    // Order by time, limit to last 50
    const q = query(messagesRef, orderBy("createdAt", "asc"), limit(50));

    // onSnapshot sets up a live listener
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const msgs = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setMessages(msgs);
      scrollToBottom();
    });

    // Clean up the listener when component unmounts or team changes
    return () => unsubscribe();
  }, [selectedRosterId]);

  const scrollToBottom = () => {
    // Small timeout to ensure DOM is ready
    setTimeout(() => {
      messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, 100);
  };

  const handleSend = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    const text = newMessage;
    setNewMessage(""); // Clear input immediately for better UX
    await sendMessage(selectedRosterId, text);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: 'calc(100vh - 100px)', maxWidth: '800px' }}>

      {/* Header & Team Selector */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px', flexShrink: 0 }}>
        <h2 style={{ margin: 0 }}>Team Chat</h2>
        <select 
          value={selectedRosterId}
          onChange={(e) => setSelectedRosterId(e.target.value)}
          style={{ padding: '8px', borderRadius: '5px', backgroundColor: '#3a3f4a', color: 'white', border: '1px solid #555' }}
        >
          {myRosters.map(r => (
            <option key={r.id} value={r.id}>{r.name}</option>
          ))}
        </select>
      </div>

      {/* Chat Window */}
      <div style={{ 
        flex: 1, 
        backgroundColor: '#222', 
        borderRadius: '8px', 
        border: '1px solid #444',
        overflowY: 'auto',
        padding: '15px',
        display: 'flex',
        flexDirection: 'column',
        gap: '10px'
      }}>
        {myRosters.length === 0 ? (
          <p style={{ color: '#888', textAlign: 'center' }}>You need to join a team to chat.</p>
        ) : messages.length === 0 ? (
          <p style={{ color: '#888', textAlign: 'center' }}>No messages yet. Say hello!</p>
        ) : (
          messages.map(msg => {
            const isMe = msg.senderId === loggedInUser.uid;
            return (
              <div key={msg.id} style={{
                alignSelf: isMe ? 'flex-end' : 'flex-start',
                maxWidth: '70%',
              }}>
                <div style={{ fontSize: '10px', color: '#888', marginBottom: '2px', textAlign: isMe ? 'right' : 'left' }}>
                  {isMe ? 'Me' : msg.senderName}
                </div>
                <div style={{
                  backgroundColor: isMe ? '#0078d4' : '#3a3f4a',
                  color: 'white',
                  padding: '8px 12px',
                  borderRadius: '15px',
                  borderTopRightRadius: isMe ? '2px' : '15px',
                  borderTopLeftRadius: isMe ? '15px' : '2px'
                }}>
                  {msg.text}
                </div>
              </div>
            );
          })
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <form onSubmit={handleSend} style={{ display: 'flex', gap: '10px', marginTop: '10px', flexShrink: 0 }}>
        <input 
          type="text" 
          placeholder="Type a message..." 
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          style={{ flex: 1, padding: '12px', borderRadius: '20px', border: 'none', backgroundColor: '#3a3f4a', color: 'white' }}
        />
        <button type="submit" style={{ 
          padding: '10px 20px', borderRadius: '20px', border: 'none', 
          backgroundColor: '#61dafb', color: '#000', fontWeight: 'bold', cursor: 'pointer'
        }}>
          Send
        </button>
      </form>

    </div>
  );
}

export default TeamChat;
